import React, { useState, useEffect} from "react";
import { Div, Button, Modal, Icon, Text, Input } from "atomize";

function LoginUpModal({ isOpen, onClose, props }) {
  const [hidePassword, showPassword] = useState(false)
  const [submit, isSubmitting] = useState(false)
  const [activeUser, setActiveUser] = useState(true)

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')

  const handleChangeEmail = (e) => {
    setEmail(e.target.value)
  }

  const handleChangePassword = (e) => {
    setPassword(e.target.value)
  }

  const checkActiveUser = () => {
    setActiveUser(!activeUser)
  }

  const loginUser = async (e) => {
    e.preventDefault()
    const url = "http://localhost:8000/api/v1/users/login"
    try {
      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
          email: email,
          password: password
        }),
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: "include",
      })
      if (response.status === 200) {

      } else {
        response.json().then((data) => {
          console.log(data);
        })
      }
    }
    catch(err) {
      console.log('Error =>', err);
    }
  }

  const onClickSubmit = () => {
    isSubmitting(true)
    setTimeout(() => {
      isSubmitting(false)
      onClose()
    }, 250)
  }

  return(
    <Modal bg="black" fontFamily="primary" align="center"
      isOpen={isOpen}
      onClose={onClose}
      rounded="0"
      minW="32rem"
      m="40rem"
    >
      <Icon
        name="Cross"
        pos="absolute"
        top="1rem"
        right="1rem"
        size="16px"
        onClick={onClose}
        cursor="pointer"
      />

    <Text textSize="display2" fontFamily="primary" textColor="white">
      SIGN UP MODAL
    </Text>

      <form onSubmit={loginUser}>
        <Div m="32px">
          <label for="email">Email</label>
          <Input
            id="email"
            onChange={handleChangeEmail}
            textSize="heading"

            type="text"
            value={email}
          />
        </Div>
        <Div m="32px">
          <label for="password">Password</label>
          <Input
            required
            id="password"
            onChange={handleChangePassword}
            textSize="heading"

            type={hidePassword ? "text" : "password"}
            value={password}
            suffix={
              <Button
                type="button"
                pos="absolute"
                onClick={() => showPassword(current => !current)}
                bg="transparent"
                w="3rem"
                top="0"
                right="0"
                rounded={{ r: "md" }}
              >
                <Icon
                  name={hidePassword ? "EyeSolid" : "Eye"}
                  color={hidePassword ? "danger800" : "success800"}
                  size="16px"
                />
              </Button>
            }
          />
        </Div>

        <Div d="flex" justify="flex-end">
          <Button
            onClick={onClose}
            fontFamily="secondary"
            textSize="heading"
            p="2rem"
            bg="gray300"
            hoverBg="gray400"
            shadow="3"
            hoverShadow="4"
            textColor="black"
            m={{ r: "1rem" }}
          >
            Close
          </Button>
          <Button type="submit" isLoading={submit} onClick={onClickSubmit} fontFamily="secondary" textSize="heading" p="2rem" bg={submit ? "danger500" : "danger800"} hoverBg="danger700" shadow="3" hoverShadow="4">
            Sign In
          </Button>
        </Div>
      </form>

    </Modal>
  )
}

export default LoginUpModal;
