import { ThemeProvider, Div, Row, Col, Text, Anchor } from "atomize";

function Footer() {
  const theme = {
    fontFamily: {
      primary: 'Teko, sans-serif',
      secondary: 'Oswald, sans-serif'
    }
  }

  return(
    <ThemeProvider theme={theme}>
      <Div bg="black" textColor="white" p={{ y: "28px" }}>
        <Row>
          <Col>
            <Text
              fontFamily="secondary"
              textSize="subheader"
            >
              <Anchor>About</Anchor> |
              <Anchor> Netflix Cliques</Anchor> |
              <Anchor> Community</Anchor> |
              <Anchor> Support</Anchor>
            </Text>
          </Col>
          <Col>
            <Anchor hoverTextColor="danger800">NETFLIX CLIQUES</Anchor>
            <Div>Â© 2021 GA Bromeliad</Div>
          </Col>
        </Row>
      </Div>
    </ThemeProvider>
  )
}

export default Footer;
