import React, { useState } from "react";
import { ThemeProvider, Modal, Container, Row, Col, Button, Text, Icon, Anchor, Image } from "atomize";
import NetflixModal from "./NetflixModal"

function MyCliques() {
  const [modal, showModal] = useState(false)
  const [cliques, setCliques] = useState([])

  const getCliques = () => {
    fetch(baseUrl + "/api/v1/cliques")
      .then((res) => {
        if (res.status === 200) {
          return res.json()
        } else {
          return []
        }
      })
      .then((data) => {
        console.log(data);
        setCliques(data.data);
      })
  }

  useEffect(() => {
    getCliques()
  }, [])

  return(
    <Container>
      <Row d="flex" justify="space-between">
        <Col d="flex" justify="flex-start">
          <Text
            textSize="subheader"
            textColor="black"
            fontFamily="code"
            textWeight="700"
          >
            MY CLIQUES
          </Text>
        </Col>
        <Col d="flex" justify="flex-end" onClick={() => showModal(true)}>
          <Anchor d="flex" flexDir="row" align="center">
            <Icon
              name="Add"
              color="success800"
              size="20px"
            />
            <Text
              textSize="subheader"
              textColor="black"
              fontFamily="code"
              textWeight="700"
            >
              ADD A NEW CLIQUE
            </Text>
          </Anchor>
        </Col>
        <NetflixModal
          isOpen={modal}
          onClose={() => showModal(false)}
        />
      </Row>
      <br></br>
      <Container>

      </Container>
      <br></br>
      <hr></hr>
    </Container>
  )
}

export default MyCliques;
