import React, { useState, useEffect} from "react";
import { Div, Button, Modal, Icon, Text, Input } from "atomize";

function SignUpModal({ isOpen, onClose }) {
  const [hidePassword, showPassword] = useState(false)
  const [submit, isSubmitting] = useState(false)

  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')

  const [value, setValue] = useState('')

  const handleChange = (event) => {
    setValue(event.target.value)
  }

  const registerUser = async (event) => {
    event.preventDefault()
    const url = "http://localhost:8000/users/signup"
    try {
      const response = await fetch(url, {
        method: 'POST',
        body: JSON.stringify({
          name: event.target.name.value,
          email: event.target.email.value,
          username: event.target.username.value,
          password: event.target.password.value,
          confirmPassword: event.target.confirmPassword.value
        }),
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: "include",
      })
      if (response.status === 200) {
        alert("User Registered!")
        function updateState() {
          setName('')
          setEmail('')
          setUsername('')
          setPassword('')
          setConfirmPassword('')
        }
      } else {
        response.json().then((data) => {
          console.log(data);
        })
      }
    }
    catch(err) {
      console.log('Error =>', err);
    }
  }

  const onClickSubmit = () => {
    isSubmitting(true)
    setTimeout(() => {
      isSubmitting(false)
      onClose()
    }, 500)
  }

  return(
    <Modal bg="black" fontFamily="primary" align="center"
      isOpen={isOpen}
      onClose={onClose}
      rounded="0"
      maxW="100vw"
      m="40rem"
    >
      <Icon
        name="Cross"
        pos="absolute"
        top="1rem"
        right="1rem"
        size="16px"
        onClick={onClose}
        cursor="pointer"
      />

    <Text textSize="display2" fontFamily="code" textColor="white">
        Sign Up Modal
      </Text>

        <Div m="32px">
          <Input
            id="name"
            onChange={(event) => handleChange(event)}
            value={name}

            value={name}
            textSize="heading"
            placeholder="Name"
          />
        </Div>
        <Div m="32px">
        <Input
          id="username"
          onChange={(e) => handleChange(e)}
          value={username}
          textSize="heading"
          placeholder="Username"
        />
        </Div>
        <Div m="32px">
        <Input
          id="email"
          onChange={(e) => handleChange(e)}
          value={email}
          textSize="heading"
          placeholder="Email"
        />
        </Div>
        <Div m="32px">
        <Input
          id="password"
          onChange={(e) => handleChange(e)}
          value={password}
          textSize="heading"
          placeholder="Password"
          type={hidePassword ? "text" : "password"}
          suffix={
            <Button
              pos="absolute"
              onClick={() => showPassword(current => !current)}
              bg="transparent"
              w="3rem"
              top="0"
              right="0"
              rounded={{ r: "md" }}
            >
              <Icon
                name={hidePassword ? "EyeSolid" : "Eye"}
                color={hidePassword ? "danger800" : "success800"}
                size="16px"
              />
            </Button>
          }
        />
        </Div>
        <Div m="32px">
        <Input
          id="confirmPassword"
          onChange={(e) => handleChange(e)}
          value={confirmPassword}
          textSize="heading"
          placeholder="Confirm Password"
          type={hidePassword ? "text" : "password"}
          suffix={
            <Button
            pos="absolute"
            onClick={() => showPassword(current => !current)}
            bg="transparent"
            w="3rem"
            top="0"
            right="0"
            rounded={{ r: "md" }}
            >
            <Icon
              name={hidePassword ? "EyeSolid" : "Eye"}
              color={hidePassword ? "danger800" : "success800"}
              size="16px"
            />
          </Button>
          }
        />
        </Div>




      <Div d="flex" justify="flex-end">
        <Button
          onClick={onClose}
          fontFamily="secondary"
          textSize="heading"
          p="2rem"
          bg="gray300"
          hoverBg="gray400"
          shadow="3"
          hoverShadow="4"
          textColor="black"
          m={{ r: "1rem" }}
        >
          Close
        </Button>
        <Button isLoading={submit} onClick={onClickSubmit} fontFamily="secondary" textSize="heading" p="2rem" bg={submit ? "danger500" : "danger800"} hoverBg="danger700" shadow="3" hoverShadow="4">
          Sign Up
        </Button>
      </Div>
    </Modal>
  )
}

export default SignUpModal;
