import React, { useState, useEffect} from "react";
import { Div, Button, Modal, Icon, Text, Input } from "atomize";

function SignUpModal({ isOpen, onClose, isSubmitting}) {
  const [password, showPassword] = useState(false)


  // useEffect(() => {
  //   console.log(password);
  // })

  return(
    <Modal bg="black" fontFamily="secondary"
      isOpen={isOpen}
      onClose={onClose}
      rounded="0"
      maxW="100vw"
      m="0"
      h="100vh"
    >
      <Icon
        name="Cross"
        pos="absolute"
        top="1rem"
        right="1rem"
        size="16px"
        onClick={onClose}
        cursor="pointer"
      />

        <Div m="16px">
          <Input
            placeholder="Name"
          />
        </Div>
        <Div m="16px">
        <Input
          placeholder="Username"
        />
        </Div>
        <Div m="16px">
        <Input
          placeholder="Email"
        />
        </Div>
        <Div m="16px">
        <Input
          placeholder="Password"
          type={password ? "text" : "password"}
          suffix={
            <Button
              pos="absolute"
              onClick={() => showPassword(current => !current)}
              bg="transparent"
              w="3rem"
              top="0"
              right="0"
              rounded={{ r: "md" }}
            >
              <Icon
                name={password ? "EyeSolid" : "Eye"}
                color={password ? "danger800" : "success800"}
                size="16px"
              />
            </Button>
          }
        />
        </Div>
        <Div m="16px">
        <Input
          placeholder="Confirm Password"
          type={password ? "text" : "password"}
          suffix={
            <Button
            pos="absolute"
            onClick={() => showPassword(current => !current)}
            bg="transparent"
            w="3rem"
            top="0"
            right="0"
            rounded={{ r: "md" }}
            >
            <Icon
              name={password ? "EyeSolid" : "Eye"}
              color={password ? "danger800" : "success800"}
              size="16px"
            />
          </Button>
          }
        />
        </Div>




      <Div d="flex" justify="flex-end">
        <Button
          onClick={onClose}
          bg="gray200"
          textColor="medium"
          m={{ r: "1rem" }}
        >
          Close
        </Button>
        <Button onClick={onClose} bg="info700">
          OK
        </Button>
      </Div>
    </Modal>
  )
}

export default SignUpModal;
