import React, { useState, useEffect} from "react";
import { Div, Button, Modal, Icon, Text, Input } from "atomize";

function SignUpModal({ isOpen, onClose }) {
  const [hidePassword, showPassword] = useState(false)
  const [submit, isSubmitting] = useState(false)

  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [confirmPassword, setConfirmPassword] = useState('')

  const [value, setValue] = useState('')

  const handleChange = (event) => {
    setValue(event.target.value)
  }


  const onClickSubmit = () => {
    isSubmitting(true)
    setTimeout(() => {
      isSubmitting(false)
      onClose()
    }, 500)
  }

  return(
    <Modal bg="black" fontFamily="primary" align="center"
      isOpen={isOpen}
      onClose={onClose}
      rounded="0"
      maxW="100vw"
      m="40rem"
    >
      <Icon
        name="Cross"
        pos="absolute"
        top="1rem"
        right="1rem"
        size="16px"
        onClick={onClose}
        cursor="pointer"
      />

    <Text textSize="display2" fontFamily="code" textColor="white">
        Sign Up Modal
      </Text>

        <Div m="32px">
          <Input
            textSize="heading"
            placeholder="Name"
          />
        </Div>
        <Div m="32px">
        <Input
          textSize="heading"
          placeholder="Username"
        />
        </Div>
        <Div m="32px">
        <Input
          textSize="heading"
          placeholder="Email"
        />
        </Div>
        <Div m="32px">
        <Input
          textSize="heading"
          placeholder="Password"
          type={hidePassword ? "text" : "password"}
          suffix={
            <Button
              pos="absolute"
              onClick={() => showPassword(current => !current)}
              bg="transparent"
              w="3rem"
              top="0"
              right="0"
              rounded={{ r: "md" }}
            >
              <Icon
                name={hidePassword ? "EyeSolid" : "Eye"}
                color={hidePassword ? "danger800" : "success800"}
                size="16px"
              />
            </Button>
          }
        />
        </Div>
        <Div m="32px">
        <Input
          textSize="heading"
          placeholder="Confirm Password"
          type={hidePassword ? "text" : "password"}
          suffix={
            <Button
            pos="absolute"
            onClick={() => showPassword(current => !current)}
            bg="transparent"
            w="3rem"
            top="0"
            right="0"
            rounded={{ r: "md" }}
            >
            <Icon
              name={hidePassword ? "EyeSolid" : "Eye"}
              color={hidePassword ? "danger800" : "success800"}
              size="16px"
            />
          </Button>
          }
        />
        </Div>




      <Div d="flex" justify="flex-end">
        <Button
          onClick={onClose}
          fontFamily="secondary"
          textSize="heading"
          p="2rem"
          bg="gray300"
          hoverBg="gray400"
          shadow="3"
          hoverShadow="4"
          textColor="black"
          m={{ r: "1rem" }}
        >
          Close
        </Button>
        <Button isLoading={submit} onClick={onClickSubmit} fontFamily="secondary" textSize="heading" p="2rem" bg={submit ? "danger500" : "danger800"} hoverBg="danger700" shadow="3" hoverShadow="4">
          Sign Up
        </Button>
      </Div>
    </Modal>
  )
}

export default SignUpModal;
