import React, { useState } from "react";
import { Modal, Div, Container, Icon, Button, Text, Input } from "atomize";

function NetflixModal({ isOpen, onClose }) {
  const [searchInput, setSearchInput] = useState('')
  const [APIData, setAPIData] = useState([])
  const [filteredResults, setFilteredResults] = useState([])

  const netflixAPI = async (e) => {
    fetch("https://unogsng.p.rapidapi.com/search?start_year=2000&limit=10" + searchInput, {
  	   "method": "GET",
  	    "headers": {
          "x-rapidapi-host": "unogsng.p.rapidapi.com",
  		    "x-rapidapi-key": "2ddf3bdb3fmsh2c7a93e3f9444adp14261cjsn73b6f6de5b56"
        }
      })
    .then(response => {
      setAPIData(response)
    	console.log(response);
    })
    .catch(err => {
    	console.error(err);
    });
  }

  const searchItems = (searchValue) => {
    setSearchInput(searchValue)
    if (searchInput !== '') {
      const filteredData = APIData.filter((item) => {
        return Object.values(item).join('').toLowerCase().includes(searchInput.toLowerCase())
      })
      setFilteredResults(filteredData)
    } else {
      setFilteredResults(APIData)
    }
  }

  const showResults = filteredResults.map((item) => {
    return(
      <Container>
        {item.img}
        {item.title}
        {item.synopsis}
      </Container>
    )
  })

  return(
    <Modal isOpen={isOpen} onClose={onClose} bg="gray300" align="start" rounded="sm" h="50rem" maxW="72rem" >
      <br></br>
      <Icon name="Cross" pos="absolute" top="1rem" right="1rem" size="16px" onClick={onClose} cursor="pointer"/>


        <Input placeholder="Search" h="3rem" onChange={(e) => searchItems(e.target.value)}
          suffix={
            <Button onClick={() => netflixAPI()} pos="absolute" bg="danger800" hoverBg="danger700" h="3rem" w="5rem" top="0" right="0" rounded={{ r: "md" }}>
              <Icon name="Search" size="36px" color="white" cursor="pointer" pos="absolute" top="50%" right="1.33rem" transform="translateY(-50%)"/>
            </Button>
          }
        />

      <showResults />
    </Modal>
  )
}

export default NetflixModal;
